CommandUIHandler.CommandAction:
javascript:function openDialog(){var options = {url: ctx.HttpRoot + '/_layouts/CrowCanyon.AdvancedPrint/PrintPage.aspx?List={ListId}&ID=' + SP.ListOperation.Selection.getSelectedItems()[0].id+'&Type=RibbonButton&SiteUrl={SiteUrl}', 
                          title:'Print',allowMaximize: true,showClose: true,width:550};
                          SP.UI.ModalDialog.commonModalDialogOpen(options.url, options, null, null);};
                          openDialog();

CommandUIHandler.EnabledScript:
javascript:function checkIsEnabled(){
              var selectedItems = SP.ListOperation.Selection.getSelectedItems();
              var count = CountDictionary(selectedItems);return (count> 0);};checkIsEnabled();

CommandUIHandler.CommandAction:
javascript:function demoCallback(dialogResult, returnValue){SP.UI.Notify.addNotification('Operation Successful!');}
              var currentCtx = SP.ClientContext.get_current();var items = SP.ListOperation.Selection.getSelectedItems(currentCtx);var myItems = '';var k;
              for (k in items){myItems += '|' + items[k].id;}
              var options = {              
                url: ctx.HttpRoot + '/_layouts/CrowCanyon.AdvancedPrint/PrintPage.aspx?items=' + myItems + '&source=' + SP.ListOperation.Selection.getSelectedList()+'&SiteUrl={SiteUrl}&ID=' + SP.ListOperation.Selection.getSelectedItems()[0].id+'&Type=RibbonButton',
                title: 'Print Items',
                allowMaximize: true,
                showClose: true,
                width: 800,
                height: 600,
                dialogReturnValueCallback: demoCallback };
              SP.UI.ModalDialog.commonModalDialogOpen(options.url, options, demoCallback, null);

